import React from "react";
import Clock from './components/Clock';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numOfClocks: 1
    }
  }

  createClock = () => {
    this.setState({
      numOfClocks: this.state.count + 1
    })
  }

  render() {
    return (
      <>
        <button onClick={this.createClock} >Add Clock</button>
        <Clock />
      </>
    )
  }
}

export default App;
